import * as React from 'react';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';

import './Hero.scss';

export default function Hero() {
  return (
    <Box id="hero">
      <Container
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          pt: { xs: 14, sm: 20 },
          pb: { xs: 8, sm: 12 },
        }}
      >
        <p className='hero__title'>夜の闇を明るくする、ロナルド吸血鬼退治事務所！</p>
        <p className='hero__text'>ロナルド吸血鬼退治事務所は、闇夜に忍び寄る恐怖からあなたを守ります。私たちは、長年の経験と知識を持つ専門家チームで構成され、吸血鬼や闇の怪物たちとの戦いに果敢に立ち向かいます。最新の技術と古くから伝わる秘術を駆使し、安全で効果的な退治を約束します。暗闇に囚われた恐怖から解放されるために、ロナルド吸血鬼退治事務所にお任せください。あなたの平和な眠りを守ります。</p>
        <button className="btn-31">
          <span className="text-container">
            <span className="text">contact</span>
          </span>
        </button>
      </Container>
    </Box>
  );
}
